const QRCodeDisplay = ({
  url,
  size = 128,
  level = "H",
  bgColor = "#FFFFFF",
  fgColor = "#000000",
}) => {
  // Added fgColor
  return (
    <QRCode
      value={url}
      size={size}
      level={level}
      bgColor={bgColor}
      fgColor={fgColor} // Use fgColor here
      includeMargin={false} // Margin will be handled by parent div
    />
  );

  return (
    <div className="flex justify-center p-4">
      <div className="p-2 border-4 border-gray-100 bg-white rounded-lg shadow-xl">
        {/* QRCodeSVG component generates an SVG image of the QR code.
                  This is great for quality and performance.
                */}
        <QRCodeSVG
          value={url}
          size={size}
          level={level}
          bgColor="#ffffff"
          fgColor="#a91b0d" // Using your brand red color
        />
      </div>
    </div>
  );
};

export default QRCodeDisplay;
